Router.setRoute('invoices');

// set default variable
Template.invoicesTemplate.limit = new ReactiveVar(invoices.limit_increase_by);

Template.registerHelper('formatDate', function (date) {
  return moment(date).format('DD-MM-YYYY');
});

Template.invoicesTemplate.helpers({
  invoiceData(){
    var react = Router.reactvar.get();
    var limit = Template.invoicesTemplate.limit.get();
    Router.addQueryParam('limit', limit);
    return invoices.byTimeRange(Router.getParams().type, Router.getQueryParams());
  },
  direction(params){
    if (params === undefined)
      return '';
    else if (params === 'ASC')
      return 'up';
    else
      return 'down';

  },
  hasMoreResults(){
    console.log(invoices.find().count());
    console.log(Template.invoicesTemplate.limit);
    return invoices.find().count() > Template.invoicesTemplate.limit;
  }
});
Template.invoicesTemplate.events({
  'click .today'() {
    Router.go({type: 'today'});
  },
  'click .week'() {
    Router.go({type: 'week'});
  },
  'click .month'() {
    Router.go({type: 'month'});
  },
  'click .all'() {
    Router.go({type: 'all'});
  },
  'click .generate'() {
    Meteor.call('generateInvoices');
  },
  'click .filterInvoiceNr'() {
    Router.go('', 'sortInvoiceNr');
  },
  'click .filterTotal'() {
    Router.go('', 'sortTotal');
  },
  'click .filterCreatedAt'() {
    Router.go('', 'sortCreatedAt');
  },
  'click .resetQuery'() {
    Router.resetQuery();
  },
  '.hasMoreResults showMoreInvoiceResults'() {
    Template.invoiceTemplate.limit.set(Template.invoiceTemplate.limit.get() + invoices.limit_increase_by);
  }
});
