Template.infiniteScrolling.onCreated(()=> {
    this.showMoreResults= ()=> {
      const target = this.$(".hasMoreResults");
      console.log(1);

      if (!target.length) return;
      let threshold = $(window).scrollTop() + $(window).height() - target.height();
      if (target.offset().top <= threshold) {
        if (!target.data("visible")) {
          this.$('hasMoreResults').trigger('showMoreInvoiceResults');
        }
      }
    };
    $(window).on('scroll', _.debounce(this.showMoreResults, 100));
});

Template.infiniteScrolling.onDestroyed(()=>{
    $(window).off('scroll', _.debounce(this.showMoreResults, 100));
});
