$(window).on('keydown', function(event) {
  if(event.keyCode === 70 && event.ctrlKey) {
    event.preventDefault();
    //show search box
  }
});

TemplateController('searchBoxCustom',
  {
    state: {
      searchConfig: {}
    },
    events: {
      'click #searchOptions .btn'(e) {
        console.log('clicked', e.currentTarget.name);
        this.state.searchConfig[e.currentTarget.name] = Template.instance().data.searchAllowedData[e.currentTarget.id];
      },
      'click #addSearchOption'() {
        $('#searchOptions').toggle();
      }
    }
  }
);
