Router.setRoute('invoices');

Template.registerHelper('formatDate', function (date) {
  return moment(date).format('DD-MM-YYYY');
});

Template.invoicesTemplate.helpers({
  invoiceData(){
    var react = Router.reactvar.get();
    return invoices.byTimeRange(Router.getParams().type, Router.getQueryParams());
  },
  direction(params){
    if (params === undefined)
      return '';
    else if (params === 'ASC')
      return 'up';
    else
      return 'down';

  },
  searchAllowedData() {
    return [
      {
        'total': {
          type: 'integer',
          placeholder: 'total'
        }
      },
      {
        'date': {
        type: 'date'
      }
    }];
  }
});
Template.invoicesTemplate.events({
  'click .today'() {
    Router.go({type: 'today'});
  },
  'click .week'() {
    Router.go({type: 'week'});
  },
  'click .month'() {
    Router.go({type: 'month'});
  },
  'click .all'() {
    Router.go({type: 'all'});
  },
  'click .generate'() {
    Meteor.call('generateInvoices');
  },
  'click .filterInvoiceNr'() {
    Router.go('', 'sortInvoiceNr');
  },
  'click .filterTotal'() {
    Router.go('', 'sortTotal');
  },
  'click .filterCreatedAt'() {
    Router.go('', 'sortCreatedAt');
  },
  'click .resetQuery'() {
    Router.resetQuery();
  }
});
