TemplateController('searchField',
  {
    helpers: {
      getSearchFieldTypeTemplate() {
        const data = Template.instance().data.data;
        switch(data.type) {
          case 'text':
            return 'searchTextBox';
          case 'date':
            return 'searchDateField';
          case 'select':
            return 'searchSelectField';
          case 'integer':
            return 'searchInteger';
        }
      }
    },
    events: {
      'click .btn'(e) {
        this.$(".btn").trigger('removeSearchOption', [e.currentTarget.name]);

      }
    }
  });
